import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { postState } from '../state/post.state';
import { ADD_POST, addPost } from '../state/post.actions';
import { AppState } from 'src/app/app.state';
import { Store } from '@ngrx/store';
import { Posts } from 'src/app/models/post.model';

@Component({
  selector: 'app-add-post',
  templateUrl: './add-post.component.html',
  styleUrls: ['./add-post.component.css']
})
export class AddPostComponent {
  public postForm!: FormGroup;
   
  
  constructor(private formBuilder: FormBuilder,private store:Store<AppState>){
 
  }
  ngOnInit():void{
    this.postForm = this.formBuilder.group({

      title:  ['',Validators.required,Validators.minLength(6),],
      description: ['',
        Validators.required,
        Validators.minLength(10),
      ],
    });

  }
  public updateUserProfile(): void {
  }
 
  onAddPost(){
console.log(this.postForm.value);
const post:Posts={
  title:this.postForm.value.title,
  description:this.postForm.value.description,

}
this.store.dispatch(addPost({post}))

  }
}
<p>add-post works!</p>
<form [formGroup]="postForm" (ngSubmit)="onAddPost()">
    <div>
    <label>Title</label>
    <input type="text" class="form-control" placeholder="title" formControlName="title"/>
</div>
<div>
    <label>description</label>
    <textarea type="text" class="form-control" placeholder="description" formControlName="description"></textarea>
    <!-- {{showDescriptionErrors()}} -->
</div>
<div>
    <button type="submit">Add Post</button>
</div>
</form>
