const _postReducer=createReducer(initialState,
  on(addPost,(state,action)=>{

  console.log(action.post);
  let post={
    ...action.post
  }
  post.id=state.posts.length+1
    return {
     ...state,
    posts: [...state.posts,post]

    }

}),
on(updatePost,(state,action)=>{
