import { Component, EventEmitter, Output } from '@angular/core';
import { Store } from '@ngrx/store';
import { decrement, increment, reset } from '../state/counter.action';

@Component({
  selector: 'app-counter-buttons',
  templateUrl: './counter-buttons.component.html',
  styleUrls: ['./counter-buttons.component.css']
})
export class CounterButtonsComponent {

  counter=0
  @Output() emit= new EventEmitter<number>();
  constructor(private store:Store<{counter:{counter:number}}>){

  }
  increment(){
    this.counter++
    this.emit.emit(this.counter)
    this.store.dispatch(increment())

  }
decrement(){
this.counter--;
this.emit.emit(this.counter)
this.store.dispatch(decrement())
}
reset(){
this.counter=0
this.emit.emit(this.counter)
this.store.dispatch(reset())
}

}
<p>counter-buttons works!</p>
<button type="text" (click)="increment()">increment</button>
<button type="text"(click)="decrement()">decrement</button>
<button type="text" (click)="reset()">reset</button>
