import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { custom } from '../state/counter.action';

@Component({
  selector: 'app-custom-input',
  templateUrl: './custom-input.component.html',
  styleUrls: ['./custom-input.component.css']
})
export class CustomInputComponent {
  customvalue=0
  constructor(private store:Store<{customvalue:{customvalue:number}}>){

  }
  customSubmit(){
    console.log(this.customvalue);
    this.store.dispatch(custom({value:this.customvalue}))


  }

}
<p>custom-input works!</p>
<input

[(ngModel)]="customvalue"
[ngModelOptions]="{ standalone: true }"/>
<button (click)="customSubmit()">submit</button>
